import React, { useState } from 'react';


function Task(){
    const [isPerson, setIsPerson] = useState(true)
    const [task, setTask] = useState({
        task_type: '',
        task_tittle: '',
        task_description: '',
        task_suburb: '',
        task_date: '',
        remuneration_type: '',
        remuneration_number: ''
    })
    const onTypeChange = (e)=>{
      if(e.target.value === "person"){
        setIsPerson(true)
        setTask((preValue)=>{
            return{
                ...preValue,
                task_type: "person"
            }
        })
      }
      else{
        setIsPerson(false)
        setTask((preValue)=>{
            return{
                ...preValue,
                task_type: "online"
            }
        })
      }
    }
    const handleChange = (event)=>{
        const {name, value} = event.target
        setTask((preValue)=>{
            return{
                ...preValue,
                [name]: value
            }
        })
    }
    const handleClick = ()=>{
        fetch('http://localhost:8000/task',{
            method: 'post',
            headers: {'Content-Type': 'application/json'},
            body: JSON.stringify({
                task_type: task.task_type,
                task_tittle: task.task_tittle,
                task_description: task.task_description,
                task_suburb: task.task_suburb,
                task_date: task.task_date,
                remuneration_type: task.remuneration_type,
                remuneration_number: task.remuneration_number
            })
        })
        .then(response => response.json())
        .then(data => console(data))
        .catch(err=>{
            console.log("Error: " + err)
        })
    }
    return (
        <div className="App">
          <Header1 />
          <TaskType typeChange={onTypeChange}/>
          <Header2 />
          <TaskDescribe onChange={handleChange}/>
          <Header3 />
          <TaskSetting isTypePerson={isPerson} onChange={handleChange}/>
          <Header4 />
          <TaskRemuneration onChange={handleChange}/>
          <Button onClick={handleClick}/>
        </div>
    );
}

export default Task
