import React, { useState } from 'react';
import Button from './Button';
import TaskDescribe from './TaskDescribe';
import TaskRemuneration from './TaskRemuneration';
import TaskSetting from './TaskSetting';
import TaskType from './TaskType';

function Task(){
    const [isPerson, setIsPerson] = useState(true)
    const [task, setTask] = useState({
        task_type: '',
        task_tittle: '',
        task_description: '',
        task_suburb: '',
        task_date: '',
        payment_type: '',
        budget: ''
    })
    const onTypeChange = (e)=>{
      if(e.target.value === "person"){
        setIsPerson(true)
        setTask((preValue)=>{
            return{
                ...preValue,
                task_type: "person"
            }
        })
      }
      else{
        setIsPerson(false)
        setTask((preValue)=>{
            return{
                ...preValue,
                task_type: "online"
            }
        })
      }
    }
    const handleChange = (event)=>{
        const {name, value} = event.target
        setTask((preValue)=>{
            return{
                ...preValue,
                [name]: value
            }
        })
    }
    const handleClick = ()=>{
        fetch('http://localhost:8000/task',{
            method: 'post',
            headers: {'Content-Type': 'application/json'},
            body: JSON.stringify({
                task_type: task.task_type,
                task_tittle: task.task_tittle,
                task_description: task.task_description,
                task_suburb: task.task_suburb,
                task_date: task.task_date,
                payment_type: task.payment_type,
                remuneration_number: task.remuneration_number
            })
        })
        .then(response => response.json())
        .then(data => console(data))
        .catch(err=>{
            console.log("Error: " + err)
        })
    }
    return (
        <div className="App">
          <div className="header-div">
            <p className="p-main">New Task</p>
        </div>
          <TaskType typeChange={onTypeChange}/>
          <div className="header-div">
            <p>Describe youe task to Experts</p>
        </div>
          <TaskDescribe onChange={handleChange}/>
          <div className="header-div">
            <p>Setting up your Task</p>
        </div>
          <TaskSetting isTypePerson={isPerson} onChange={handleChange}/>
          <div className="header-div">
            <p>Suggest how much</p>
        </div>
          <TaskRemuneration onChange={handleChange}/>
          <Button onClick={handleClick}/>
        </div>
    );
}

export default Task
